import Api from"../../scripts/global/api";class databaserestorant{static async daftarrestorant(){const a=await fetch(Api.DaftarRestorant);if(a.ok)return(await a.json()).restaurants;throw new Error("Gagal mengambil data daftar restoran.")}static async detailrestorant(a){const t=await fetch(Api.detail(a));if(t.ok)return(await t.json()).restaurant;throw new Error("Gagal mengambil data detail restoran.")}}export default databaserestorant;